    text-decoration: none;
    color: #222;
    display: block;
    width: 100%;
}

img {
    display: block;
    width: 100%;
}

.title {
    font: {
        size: 28px;
        weight: bold;
    }
    text-align: center;
}

@mixin size($width, $height) {
    width: $width;
    height: $height;
}

@mixin flex_style {
    display: flex;
    align-items: center;
}

@mixin line($height) {
    line-height: $height;
    text-align: center;
}
$color: #fd8234;

.header {
    @include size(100%, 80px);
    background-color: transparent;
    position: fixed;
    top: 0;
    left: 0;
    z-index: 999;

    font: {
        size: 15px;
    }

    .header_inner {
        @include size(100%, 80px);
        @include flex_style();
        padding: 0 25px;
        justify-content: space-between;

        .header_left {
            position: relative;
            @include flex_style();

            .logo {
                width: 52px;
                margin-right: 35px;
            }
            .lnb {
                ul {
                    @include flex_style();

                    li {
                        margin-right: 35px;

                        &:last-child {
                            margin-right: 0;
                        }
                    }
                }
            }

            .lnb_nav {
                position: fixed;
                top: 80px;
                left: 0;
                background-color: #fff;
