@mixin size($width, $height) {
    width: $width;
    height: $height;
}

@mixin flex_style {
    display: flex;
    align-items: center;
}

@mixin line($height) {
    line-height: $height;
    text-align: center;
}
$color: #fd8234;

/* -------------header--------------- */
.header {
    position: absolute;
    top: 0;
    right: 0;
    left: 0;
    @include size(100%, 80px);
    background-color: transparent;
    border-bottom: 1px solid #eee;
    z-index: 999;

    font: {
        size: 15px;
    }

    .header_inner {
        @include size(100%, 80px);
        @include flex_style();
        padding: 0 25px;
        justify-content: space-between;

        ul {
            li {
                &:hover {
                    > a {
                        color: #fd8234;
                        transition: all 0.3s;
                    }
                }
            }
        }

        .header_left {
            position: relative;
            @include flex_style();

            .logo {
                width: 52px;
                margin-right: 35px;
            }
            .lnb {
                > ul {
                    @include flex_style();
                    height: 80px;

                    > li {
                        height: 100%;
                        line-height: 80px;
                    }

                    .menu {
                        &:hover {
                            .aside {
                                left: 0;
                                transition: all 0.3s;
                            }
                        }
                        .aside {
                            position: fixed;
                            top: 80px;
                            left: -435px;
                            background-color: #fff;
                            height: calc(100vh - 80px);
                            color: #261c13;
                            font-family: 'Noto Sans KR', sans-serif;
                            z-index: 1000;
                            font: {
                                size: 14px;
                                weight: 300;
                            }

                            li {
                                &:hover {
                                    background-color: #efe8da;
                                    transition: all 0.3s;
                                }
                            }

                            .top_lnb {
                                height: 60px;
                                line-height: 60px;

                                ul {
                                    @include flex_style();

                                    li {
                                        width: 50%;
                                        padding: 0 20px;
                                        border-bottom: 1px solid #eee;
                                        margin-right: 0;
                                    }
                                }
                            }
                            .bottom_lnb {
                                li {
                                    height: 60px;
                                    width: 435px;
                                    padding: 0 20px;
                                    line-height: 60px;
                                    margin-right: 0;
                                    border-bottom: 1px solid #eee;
                                }
                            }
                        }
                    }

                    .brand_story {
                        position: relative;

                        &:hover {
                            .story_box {
                                visibility: visible;
                                opacity: 1;
                                transition: all 0.3s;
                            }
                        }

                        .story_box {
                            visibility: hidden;
                            opacity: 0;

                            ul {
                                position: fixed;
                                top: 80px;
                                left: 0;
                                @include size(100%, 60px);
                                @include line(60px);
                                display: flex;
                                background-color: #fff;
                                border-bottom: 1px solid #eee;
                                padding-left: 184px;
                            }
                        }
                    }

                    li {
                        margin-right: 35px;

                        &:last-child {
                            margin-right: 0;
                        }
                    }
                }
            }
        }

        .header_right {
            @include flex_style();

            ul {
                @include flex_style();

                li {
                    margin-right: 35px;

                    &:last-child {
                        margin-right: 0;
                    }

                    &.search_icon {
                        position: relative;
                        @include size(160px, 25px);
                        border-radius: 20px;
                        background-color: $color;

                        input {
                            background-color: $color;
                            border: none;
                            outline: none;
                            @include size(130px, 25px);
                            border-radius: 20px;
                            padding-left: 15px;
                        }

                        button {
                            position: absolute;
                            top: 5px;
                            right: 7px;
                            background-color: $color;
                            border: none;
                            outline: none;
                            border-radius: 20px;
                            cursor: pointer;
                            color: #fff;
                        }
                    }
                }
            }
        }
    }
    &.is-sticky {
        //position: fixed;
        box-shadow: 0 5px 16px rgba(0, 0, 0, 0.1);
        backdrop-filter: blur(10px);
        animation: slideDown 0.35s ease-out;
    }
}
@keyframes slideDown {
    from {
        transform: translateY(-100%);
    }
    to {
        transform: translateY(0);
    }
}

